from django.urls import pathfrom .import viewsfrom django.views.generic.detail import DetailViewfrom django.views.generic import ListViewfrom .models import Expert, Reviewurlpatterns = [    path('', ListView.as_view(queryset = Review.objects.all(), template_name='index/index.html'), name = 'expert'),    path('expert/<int:pk>', DetailView.as_view(model = Expert, template_name='index/detail.html')),    path('contacts/', views.contacts),    path('about/', views.about),    path('work/', views.work),    path('experts/', views.experts),    path('services/', views.services),    path('review/', views.review)]